import React, {useState, useEffect, useRef, useContext} from 'react'
import {useHistory, Link, useParams} from "react-router-dom"
import datasetsAPI from '../API/datasets'
import globalAPI from '../API/global'
import ViewData from '../Components/View-Data';
import { OpenItemsContext } from '../Contexts/openItemsContext';
import ItemRowCard from '../Components/Item-Row-Card'
import ItemSquareCard from '../Components/Item-Square-Card'
import BookmarkIcon from '@mui/icons-material/Bookmark';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';

const Workspace = ({currentUser}) => {
    const [loaded, setLoaded] = useState(false)
    const [dataset, setDataset] = useState()
    const [workspaces, setWorkspaces] = useState()
    const [finishedWorkspaces, setFinishedWorkspaces] = useState(false)
    const [displayType, setDisplayType] = useState("data")
    const [dataTable, setDataTable] = useState()
    const [start, setStart] = useState(0)
    const [end, setEnd] = useState(30)
    const [dataFile, setDataFile] = useState()
    const [uploadData, setUploadData] = useState()
    const [row, setRow] = useState()
    const [maxRows, setMaxRows] = useState()
    const [page, setPage] = useState(1)
    const [rowFormat, setRowFormat] = useState(false)
    const [replaceData, setReplaceData] = useState(false)
    const [editData, setEditData] = useState(false)
    const [date, setDate] = useState("");
    const [copyData, setCopyData] = useState(true)
    const [bookmarked, setBookmarked] = useState()
    const [upvoted, setUpvoted] = useState()
    const [upvotes, setUpvotes] = useState()
    const [visibility, setVisibility] = useState()
    const [comments, setComments] = useState()
    const [comment, setComment] = useState("")
    const [section, setSection] = useState("data")
    const [title, setTitle] = useState("")
    const [description, setDescription] = useState("")
    const [image, setImage] = useState();
    const {addOpenItems} = useContext(OpenItemsContext);
    const copyInterval = useRef(0)
    const datasetID = useParams().id;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const dataset = await datasetsAPI.get(`/${datasetID}`);
                const comments = await globalAPI.get(`/comment/${datasetID}?type=dataset`);
                const workspaces = await datasetsAPI.get(`/workspaces?id=${datasetID}&date=${new Date().toISOString()}`);

                if (workspaces.data.data.length < 21) {
                    setFinishedWorkspaces(true)
                }

                if (dataset.data.data.creator === currentUser.id) {
                    addOpenItems(dataset.data.data._id, dataset.data.data.title, dataset.data.data.type)
                }

                setDataset(dataset.data.data);
                setBookmarked(dataset.data.data.bookmarked)
                setUpvoted(dataset.data.data.upvoted)
                setUpvotes(dataset.data.data.upvotes)
                setVisibility(dataset.data.data.visibility)
                setTitle(dataset.data.data.title)
                setDescription(dataset.data.data.description)
                setWorkspaces(workspaces.data.data)
                setComments(comments.data.data)

                fetch(`http://127.0.0.1:5000/files/${dataset.data.data.data}.csv`)
                    .then(response => response.text())
                    .then(text => {
                        setDataTable(text)
                        setMaxRows(text.slice(text.indexOf('\n')+1).split('\n').length)
                        setLoaded(true)
                    })
            } catch (err) {}
        }
        fetchData();
    }, [])

    console.log(comments)

    useEffect(() => {
        if (loaded) {
            const updatedDate = new Date(dataset.updated);
            const currentDate = new Date();

            if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 365) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 365)).toString()} years ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 30) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString())} months ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 1) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24))).toString()} days ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600) >= 1) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600))).toString()} hours ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60) >= 1) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60))).toString()} minutes ago`)
            } else {
                setDate("Updated just now")
            }
        }
    }, [loaded])

    useEffect(() => {
        if (replaceData && uploadData !== undefined) {
            const file = uploadData;
            const reader = new FileReader();

            reader.onload = function(e) {
                setDataTable(e.target.result);
            }

            reader.readAsText(file)
        }
    }, [uploadData])

    const fetchDataWorkspaces = async (date) => {
        if (!finishedWorkspaces) {
            try {
                const fetchedWorkspaces = await datasetsAPI.get(`/workspaces?date=${date}`);;

                if (fetchedWorkspaces.data.data.length < 21) {
                    setFinishedWorkspaces(true)
                }

                setWorkspaces(items => [...items, ...fetchedWorkspaces.data.data]);
            } catch (err) {}
        }
    }

    const loadMore = () => {
        if (workspaces.length !== 0) {
            {fetchDataWorkspaces(workspaces[workspaces.length-1].createdAt)}
        }
    };

    const copiedInterval = () => {
        clearInterval(copyInterval.current)
        navigator.clipboard.writeText(dataset.data);
        setCopyData(false);
        copyInterval.current = setInterval(() => {
            setCopyData(true);
        }, 800)
        return ()=> {clearInterval(copyInterval.current)};
    }

    const updateUpvote = async () => {
        try {
            await globalAPI.put(`/upvote/${dataset._id}?type=${dataset.type}&state=${upvoted}`);

            if (upvoted) {
                setUpvotes(state => state-1)
            } else {
                setUpvotes(state => state+1)
            }

            setUpvoted(state => !state)
        } catch (err) {}
    }

    const updateBookmark = async () => {
        try {
            await globalAPI.put(`/bookmark/${dataset._id}?type=${dataset.type}&state=${bookmarked}`);

            setBookmarked(state => !state)
        } catch (err) {}
    }

    const updateVisibility = async () => {
        try {
            await globalAPI.put(`/visibility/${dataset._id}?type=${dataset.type}&state=${visibility}`);

            setVisibility(state => !state)
        } catch (err) {}
    }

    const addComment = async (e) => {
        e.preventDefault()

        try {
            await globalAPI.put(`/comment/${datasetID}?type=dataset`, {
                comment: comment
            });

            setComments([{
                user: {name: currentUser.name},
                comment: comment
            }, ...comments])
            setComment("")
        } catch (err) {}
    }

    const fetchRow = () => {
        if (!isNaN(row) && row !== "") {
            setStart(row-1)
            setEnd(row)
        } else {
            setRow("")
            setStart(0)
            setEnd(30)
        }
    }

    const cancelRow = () => {
        setRow("")
        setStart(0)
        setEnd(30)
    }

    const previousPage = () => {
        if (page > 1) {
            setPage(state => state-1)
        }
        setStart((page-1)*30)
        setEnd(page*30)
    }

    const nextPage = () => {
        if (page*30 < maxRows && maxRows > 30) {
            setPage(state => state+1)
            setStart((page)*30)
            setEnd((page+1)*30)
        }
    }

    const commentDate = (date) => {
        const updatedDate = new Date(date);
        const currentDate = new Date();

        if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 365) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 365)).toString()} years ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 30) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString())} months ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 1) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24))).toString()} days ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600) >= 1) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600))).toString()} hours ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60) >= 1) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60))).toString()} minutes ago`
        } else {
            return "Posted just now"
        }
    }

    const updateWorkspaceSettings = () => {

    }

    const deleteWorkspace = () => {

    }

    return (
        <>
            {loaded &&
                <>
                    {dataset.self ?
                        <div className="sidebar-body"> 
                            <div className="self-dataset-sidebar">
                                <h1>{dataset.title}</h1>
                                <button className={section === "data" ? "blue-button" : "grey-button"} onClick={() => {setSection("data")}}>Data</button>
                                <button className={section === "experiments" ? "blue-button" : "grey-button"} onClick={() => {setSection("experiments")}}>Experiments</button>
                                <button className={section === "settings" ? "blue-button" : "grey-button"} onClick={() => {setSection("settings")}}>Settings</button>
                                <button className={section === "comments" ? "blue-button" : "grey-button"} onClick={() => {setSection("comments")}}>Comments</button>
                            </div>
                            <div className="inner-body">
                                {section === "data" ?
                                    <div className="new-item-import">
                                        <input type="file" 
                                                name="data"
                                                key={Date.now()}
                                                onChange={e => {setDataFile(e.target.files[0])}} />
                                        {dataFile !== undefined && dataTable !== undefined &&
                                            <div className="new-item-data">
                                                <div className="new-item-data-information">
                                                    <p className="new-item-filename">File: {dataFile.name}</p>
                                                    <span />
                                                    <div className="new-item-data-table-pagination">
                                                        <ArrowBackIosNewIcon className="new-item-data-table-pagination-icon" onClick={() => {previousPage()}} />
                                                        <p>Page {page} / Page {Math.ceil(maxRows/30)}</p>
                                                        <ArrowForwardIosIcon className="new-item-data-table-pagination-icon" onClick={() => {nextPage()}} />
                                                    </div>
                                                </div>
                                                <div className="new-item-data-table">
                                                    <ViewData dataTable={dataTable} start={start} end={end} key={new Date().getTime()} />
                                                </div>
                                            </div>
                                        }
                                    </div>
                                : section === "experiments" ?
                                    <div className="self-dataset-body">
                                    </div>
                                : section === "settings" ?
                                    <div className="self-dataset-settings">
                                        <form className="self-dataset-settings-form" method="PUT" onSubmit={updateWorkspaceSettings}>
                                            <label>Title</label>
                                            <input placeholder="Title" value={title} onChange={e => {setTitle(e.target.value)}} />
                                            <label>Description</label>
                                            <textarea placeholder="Description" value={description} onChange={e => {setDescription(e.target.value)}} />
                                            <label>Picture</label>
                                            <input type="file" 
                                                    className="self-dataset-settings-picture"
                                                    name="image" 
                                                    onChange={e => {setImage(e.target.files[0])}} />
                                            <div className="self-dataset-settings-visibility">
                                                <label className="self-dataset-settings-visibility-label">Public?</label>
                                                <input type="checkbox" 
                                                        className="self-dataset-settings-visibility-input"
                                                        onClick={() => {updateVisibility()}}
                                                        checked={visibility} />
                                            </div>
                                            <div className="self-dataset-settings-buttons">
                                                <button className="blue-button" disabled={title === "" || description === ""}>Save Changes</button>
                                                <button className="white-button" type="button" onClick={() => {deleteWorkspace()}}>Delete</button>
                                            </div>
                                        </form>
                                    </div>
                                : 
                                    <div className="self-dataset-comments">
                                        {comments.length === 0 ?
                                            <p className="self-dataset-comments-none">No Comments</p>
                                        :
                                            <>
                                                {comments.map((comment, i) => {
                                                    return (
                                                        <div className="comment-card" key={i}>
                                                            <div>
                                                                <p className="comment-card-user">{comment.user.name}</p>
                                                                <p className="comment-card-date">{commentDate(comment.createdAt)}</p>
                                                            </div>
                                                            <p className="comment-card-comment">{comment.comment}</p>
                                                        </div>
                                                    )
                                                })}
                                            </>
                                        }
                                    </div>
                                }
                            </div>
                        </div>
                    :
                        <div className="width-body">  
                            <div className="other-dataset-body">
                                <div className="other-dataset-top">
                                    <img className="other-dataset-picture" src={`http://localhost:4000/images/${dataset.picture}`} />
                                    <div className="other-dataset-heading">
                                        <img src="http://localhost:3000/dataset.png" />
                                        <h1>{dataset.title}</h1>
                                    </div>
                                    <div>
                                        <p className="other-dataset-meta">{dataset.creatorName.name}</p>
                                        <p className="other-dataset-meta">{date}</p>
                                        <BookmarkIcon className={`other-dataset-icon ${bookmarked ? "blue" : "grey"}`} onClick={() => {updateBookmark()}} />
                                        <ThumbUpIcon className={`other-dataset-icon ${upvoted ? "blue" : "grey"}`} onClick={() => {updateUpvote()}} />
                                        <p className={`other-dataset-upvotes ${upvoted ? "blue" : "grey"}`}>{upvotes}</p>
                                    </div>
                                    <p className="other-dataset-description">{dataset.description}</p>
                                    <select className="other-dataset-select" onChange={e => {setDisplayType(e.target.value)}}>
                                        <option value="data">Data</option>
                                        <option value="workspace">Workspaces</option>
                                        <option value="comment">Comments</option>
                                    </select>
                                </div>
                                <div className="other-dataset-bottom">
                                    {displayType === "data" ? 
                                        <> 
                                            <div className="other-dataset-data">
                                                <input placeholder="Row number" value={row} onChange={e => {setRow(e.target.value)}} />
                                                <button onClick={() => {fetchRow()}} className="blue-button other-dataset-data-fetch">Fetch</button>
                                                <button onClick={() => {cancelRow()}} className="grey-button other-dataset-data-cancel-fetch">Cancel</button>
                                            </div>
                                            <div className="other-dataset-data-table-pagination">
                                                <ArrowBackIosNewIcon className="other-dataset-data-table-pagination-icon" onClick={() => {previousPage()}} />
                                                <p>Page {page} / Page {Math.ceil(maxRows/30)}</p>
                                                <ArrowForwardIosIcon className="other-dataset-data-table-pagination-icon" onClick={() => {nextPage()}} />
                                                <div className="other-dataset-copy">
                                                    {copyData ? <p>Data ID</p> : <p>Copied</p>}
                                                    <button disabled={!copyData} onClick={() => {copiedInterval()}}>
                                                        <ContentCopyIcon className="other-dataset-copy-icon" />
                                                    </button>
                                                </div>
                                                <a href={`http://127.0.0.1:5000/files/${dataset.data}.csv`} download>Download</a>
                                            </div>
                                            <div className="other-dataset-data-table">
                                                <ViewData dataTable={dataTable} start={start} end={end} key={new Date().getTime()} />
                                            </div>
                                        </>
                                    : displayType === "workspace" ?
                                        <>
                                            <div className="other-dataset-workspaces">
                                                <p>{`${workspaces.length} Workspaces use this dataset`}</p>
                                                <img src="http://localhost:3000/List.png" className="other-dataset-workspaces-row-icon" onClick={() => {setRowFormat(true)}} />
                                                <img src="http://localhost:3000/Grid.png" className="other-dataset-workspaces-grid-icon" onClick={() => {setRowFormat(false)}} />
                                            </div>
                                            <div className="other-dataset-workspaces-list">
                                                {workspaces.length > 0 &&
                                                    <>
                                                        {workspaces.map((workspace, i) => {
                                                            return rowFormat ? <ItemRowCard item={workspace} creator={workspace.creatorName.name} currentUserID={currentUser.id} created={workspace.creator === currentUser.id} key={i} /> : <ItemSquareCard item={workspace} creator={workspace.creatorName.name} currentUserID={currentUser.id} created={workspace.creator === currentUser.id} key={i} />
                                                        })}
                                                    </>
                                                }
                                                {workspaces.length >= 0 && finishedWorkspaces ?
                                                    <p className="end-items">No more workspaces</p>
                                                    :
                                                    <p className="load-items" onClick={() => {loadMore()}}>Load more</p>
                                                }
                                            </div>
                                        </>
                                    :
                                        <>
                                            <form className="other-dataset-comment-form" method="PUT" onSubmit={addComment}>
                                                <p className="other-dataset-comment">Leave a Comment</p>
                                                <textarea className="other-dataset-comment-input" placeholder="Write here" value={comment} onChange={e => {setComment(e.target.value)}} />
                                                <button className="blue-button">Comment</button>
                                            </form>
                                            <div className="other-dataset-comments">
                                                {comments.map((comment, i) => {
                                                    return (
                                                        <div className="comment-card" key={i}>
                                                            <div>
                                                                <p className="comment-card-user">{comment.user.name}</p>
                                                                <p className="comment-card-date">{commentDate(comment.createdAt)}</p>
                                                            </div>
                                                            <p className="comment-card-comment">{comment.comment}</p>
                                                        </div>
                                                    )
                                                })}
                                            </div>
                                        </>
                                    }
                                </div>
                            </div>
                        </div>
                    }
                </>
            }
        </>
    )
}

export default Workspace
