import React, {useState, useEffect} from 'react'
import { Link, useHistory } from 'react-router-dom'
import globalAPI from '../API/global'
import ItemFeedCard from '../Components/Item-Feed-Card'
import Shortcut from '../Components/Shortcut';
import SearchIcon from '@mui/icons-material/Search';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

const Home = ({setSearchPhrase, currentUser}) => {
    const [recent, setRecent] = useState()
    const [loaded, setLoaded] = useState(false)
    const [input, setInput] = useState("");
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const recent = await globalAPI.get("/feed");

                setRecent(recent.data.data);
                setLoaded(true);
            } catch (err) {}
        }
        fetchData();
    }, [])

    const searchFunctionKey = (e) => {
        if (e.key === "Enter" && input !== "") {
            setSearchPhrase(input);
            history.push(`/search-results/${input}`);
        }
    }

    const searchFunctionButton = () => {
        if (input !== "") {
            setSearchPhrase(input);
            history.push(`/search-results/${input}`);
        }
    }

    return (
        <>
            {loaded &&
                <div className="main-body">
                    <div className="sidebar">

                    </div>
                    <div className="inner">

                    </div>
                </div>
            }
        </>
    )
}

export default Home
