import React, {useState, useEffect, useRef, useContext} from 'react'
import {Link, useParams} from "react-router-dom"
import workspacesAPI from '../API/workspaces'
import globalAPI from '../API/global'
import ViewData from '../Components/View-Data';
import { OpenItemsContext } from '../Contexts/openItemsContext';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import VisibilityIcon from '@mui/icons-material/Visibility';

const Workspace = ({currentUser}) => {
    const [loaded, setLoaded] = useState(false)
    const [exist, setExist] = useState()
    const [noData, setNoData] = useState()
    const [workspace, setWorkspace] = useState()
    const [dataTable, setDataTable] = useState()
    const [dataID, setDataID] = useState("")
    const [changedData, setChangedData] = useState(false)
    const [changedSettings, setChangedSettings] = useState(false)
    const [date, setDate] = useState("");
    const [start, setStart] = useState(0)
    const [end, setEnd] = useState(30)
    const [row, setRow] = useState()
    const [maxRows, setMaxRows] = useState()
    const [page, setPage] = useState(1)
    const [copyData, setCopyData] = useState(true)
    const [bookmarked, setBookmarked] = useState()
    const [upvoted, setUpvoted] = useState()
    const [upvotes, setUpvotes] = useState()
    const [picture, setPicture] = useState()
    const [visibility, setVisibility] = useState()
    const [comments, setComments] = useState()
    const [comment, setComment] = useState("")
    const [section, setSection] = useState("data")
    const [title, setTitle] = useState("")
    const [description, setDescription] = useState("")
    const [image, setImage] = useState();
    const {addOpenItems} = useContext(OpenItemsContext);
    const copyInterval = useRef(0)
    const workspaceID = useParams().id;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const workspace = await workspacesAPI.get(`/${workspaceID}`);
                const comments = await globalAPI.get(`/comment/${workspaceID}?type=workspace`);

                if (workspace.data.data.creator === currentUser.id) {
                    addOpenItems(workspace.data.data._id, workspace.data.data.title, workspace.data.data.type)
                }

                setWorkspace(workspace.data.data);
                setBookmarked(workspace.data.data.bookmarked)
                setUpvoted(workspace.data.data.upvoted)
                setPicture(workspace.data.data.picture)
                setUpvotes(workspace.data.data.upvotes)
                setVisibility(workspace.data.data.visibility)
                setTitle(workspace.data.data.title)
                setDescription(workspace.data.data.description)
                setComments(comments.data.data)

                fetch(`http://127.0.0.1:5000/files/${workspace.data.data.workspace.dataset.data}.csv`)
                    .then(response => response.text())
                    .then(text => {
                        setDataTable(text)
                        setExist(true)
                        setNoData(false)
                        setLoaded(true)
                    }).catch(function() {
                        setExist(true)
                        setNoData(true)
                        setLoaded(true)
                    });
            } catch (err) {
                setExist(false)
                setLoaded(true)
            }
        }
        fetchData();
    }, [])    

    useEffect(() => {
        if (loaded && exist) {
            const updatedDate = new Date(workspace.updated);
            const currentDate = new Date();

            if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 365) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 365)).toString()} years ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 30) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString())} months ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 1) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24))).toString()} days ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600) >= 1) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600))).toString()} hours ago`)
            } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60) >= 1) {
                setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60))).toString()} minutes ago`)
            } else {
                setDate("Updated just now")
            }
        }
    }, [loaded])

    const existingWorkspace = () => {
        fetch(`http://127.0.0.1:5000/files/${dataID}.csv`)
            .then(response => response.text())
            .then(text => {setDataTable(text)})
    }

    const copiedInterval = () => {
        clearInterval(copyInterval.current)
        navigator.clipboard.writeText(workspace.data);
        setCopyData(false);
        copyInterval.current = setInterval(() => {
            setCopyData(true);
        }, 800)
        return ()=> {clearInterval(copyInterval.current)};
    }

    const updateUpvote = async () => {
        try {
            await workspacesAPI.put(`/upvote/${workspaceID}?state=${upvoted}`);

            if (upvoted) {
                setUpvotes(state => state-1)
            } else {
                setUpvotes(state => state+1)
            }

            setUpvoted(state => !state)
        } catch (err) {}
    }

    const updateBookmark = async () => {
        try {
            await workspacesAPI.put(`/bookmark/${workspaceID}?state=${bookmarked}`);

            setBookmarked(state => !state)
        } catch (err) {}
    }

    const updateVisibility = async () => {
        try {
            await workspacesAPI.put(`/visibility/${workspace._id}`);

            setVisibility(state => !state)
        } catch (err) {}
    }

    const addComment = async (e) => {
        e.preventDefault()

        try {
            await workspacesAPI.put(`/comment/${workspaceID}`, {
                comment: comment
            });

            setComments([{
                user: {name: currentUser.name},
                comment: comment
            }, ...comments])
            setComment("")
        } catch (err) {}
    }

    const commentDate = (date) => {
        const updatedDate = new Date(date);
        const currentDate = new Date();

        if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 365) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 365)).toString()} years ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 30) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString())} months ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 1) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24))).toString()} days ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600) >= 1) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600))).toString()} hours ago`
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60) >= 1) {
            return `Posted ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60))).toString()} minutes ago`
        } else {
            return "Posted just now"
        }
    }

    const updateSettings = () => {

    }

    const deleteWorkspace = () => {

    }

    return (
        <>
            {loaded && exist ?
                <div className="width-body">  
                    <div className="dataset-body">
                        <div className="dataset-top">
                            <img className="dataset-picture" src={`http://localhost:4000/images/${picture}`} />
                            {workspace.self && 
                                <input className="dataset-image-input"
                                        type="file" 
                                        name="image" 
                                        onChange={e => {
                                            setImage(e.target.files[0])
                                            {!changedSettings && setChangedSettings(true)}
                                        }} />
                            }
                            <div className="dataset-heading">
                                {workspace.self ? 
                                    <input className="dataset-title-input"
                                            placeholder="Title" 
                                            value={title}
                                            onChange={e => {
                                                setTitle(e.target.value)
                                                {!changedSettings && setChangedSettings(true)}
                                            }} /> 
                                : 
                                    <>
                                        <img src="http://localhost:3000/workspace.png" />
                                        <h1>{workspace.title}</h1>
                                    </>
                                }
                            </div>
                            <div>
                                {!workspace.self && <p className="dataset-meta">{workspace.creatorName.name}</p>}
                                <p className="dataset-meta">{date}</p>
                                <span />
                                {!workspace.self && <BookmarkIcon className={`dataset-icon ${bookmarked ? "blue" : "grey"}`} onClick={() => {updateBookmark()}} />}
                                {workspace.self && 
                                    <>
                                        {visibility ? 
                                            <VisibilityIcon className="dataset-visibility" onClick={() => {updateVisibility()}} />
                                        :
                                            <VisibilityOffIcon className="dataset-visibility" onClick={() => {updateVisibility()}} />
                                        }
                                    </>
                                }
                                <ThumbUpIcon className={`dataset-icon ${upvoted ? "blue" : "grey"}`} onClick={() => {updateUpvote()}} />
                                <p className={`dataset-upvotes ${upvoted ? "blue" : "grey"}`}>{upvotes}</p>
                            </div>
                            {workspace.self ? 
                                <>
                                    <textarea className="dataset-description-input"
                                                placeholder="Description" 
                                                value={description}
                                                onChange={e => {
                                                    setDescription(e.target.value)
                                                    {!changedSettings && setChangedSettings(true)}
                                                }} /> 
                                    <div className="dataset-middle">
                                        <button className="dark-grey-button dataset-delete"
                                                onClick={e => {deleteWorkspace(e)}}>Delete</button>
                                        <button className="blue-button"
                                                disabled={!changedSettings}
                                                onClick={e => {updateSettings(e)}}>Save Changes</button>
                                    </div>
                                </>
                            : 
                                <p className="dataset-description">{workspace.description}</p>
                            }
                            <select className="dataset-select" onChange={e => {setSection(e.target.value)}}>
                                <option value="data">Data</option>
                                <option value="workspace">Workspaces</option>
                                <option value="comment">Comments</option>
                            </select>
                        </div>
                        <div className="dataset-bottom">
                            {section === "data" ? 
                                <> 
                                    {noData ?
                                        <p className="item-exist">Cannot find dataset</p>
                                    :   
                                        <>
                                            <div className="dataset-options">
                                                <input className="new-workspace-import-existing"
                                                        placeholder="Data ID"
                                                        onChange={e => {setDataID(e.target.value)}}
                                                        value={dataID} />
                                                <button className="white-button dataset-replace-button"
                                                        onClick={() => {
                                                            setDataID(undefined)
                                                            setChangedData(false)
                                                        }}
                                                        disabled={!changedData}>Clear</button>
                                                <button className="blue-button dataset-replace-button"
                                                        disabled={dataID === ""}
                                                        onClick={() => {existingWorkspace()}}>Import</button>
                                                <div className="dataset-copy">
                                                    {copyData ? <p>Data ID</p> : <p>Copied</p>}
                                                    <button disabled={!copyData} onClick={() => {copiedInterval()}}>
                                                        <ContentCopyIcon className="dataset-copy-icon" />
                                                    </button>
                                                </div>
                                                <a href={`http://127.0.0.1:5000/files/${workspace.dataset.data}.csv`} download>Download</a>
                                            </div>
                                            <div className="dataset-data-table-pagination">
                                                <input placeholder="Row number" value={row} onChange={e => {setRow(e.target.value)}} />
                                                <button onClick={() => {fetchRow()}} className="blue-button dataset-data-fetch">Fetch</button>
                                                <button onClick={() => {cancelRow()}} className="white-button dataset-data-cancel-fetch">Cancel</button>
                                                <span />
                                                <ArrowBackIosNewIcon className="dataset-data-table-pagination-icon" onClick={() => {previousPage()}} />
                                                <p>Page {page} / {Math.ceil(maxRows/30)}</p>
                                                <ArrowForwardIosIcon className="dataset-data-table-pagination-icon" onClick={() => {nextPage()}} />
                                            </div>
                                            <div className="dataset-data-table">
                                                <ViewData dataTable={dataTable} start={start} end={end} key={refreshTable} />
                                            </div>
                                        </>
                                    }
                                </>
                            : 
                                <>
                                    <form className="dataset-comment-form" method="PUT" onSubmit={addComment}>
                                        <p className="dataset-comment">Leave a Comment</p>
                                        <textarea className="dataset-comment-input" placeholder="Write here" value={comment} onChange={e => {setComment(e.target.value)}} />
                                        <button className="blue-button">Comment</button>
                                    </form>
                                    <div className="dataset-comments">
                                        {comments.map((comment, i) => {
                                            return (
                                                <div className="comment-card" key={i}>
                                                    <div>
                                                        <p className="comment-card-user">{comment.user.name}</p>
                                                        <p className="comment-card-date">{commentDate(comment.createdAt)}</p>
                                                    </div>
                                                    <p className="comment-card-comment">{comment.comment}</p>
                                                </div>
                                            )
                                        })}
                                    </div>
                                </>
                            }
                        </div>
                    </div>
                </div>
            : loaded && !exist &&
                <div className="width-body">  
                    <p className="item-exist">Cannot find workspace</p>
                </div>
            }   
        </>
    )
}

export default Workspace
