import React, {useState, useContext, useEffect} from 'react'
import { useLocation, Link, useHistory } from 'react-router-dom'
import CloseIcon from '@mui/icons-material/Close';
import { OpenItemsContext } from '../Contexts/openItemsContext';

const HeaderOpenItems = ({item}) => {
    const [itemID, setItemID] = useState("")
    const history = useHistory();
    const location = useLocation();
    const {removeOpenItems} = useContext(OpenItemsContext);

    useEffect(() => {
        if (item.type === "workspace") {
            setItemID(location.pathname.substring(11))
        } else {
            setItemID(location.pathname.substring(9))
        }
    }, [])

    const closeItem = () => {
        removeOpenItems(item.id)

        if (itemID === item.id) {
            history.replace("/home")
        }
    }

    return (
        <div className="item-header">
            <Link to={item.type === "item" ? `/item/${item.id}` : `/dataset/${item.id}`} className="item-header-link">
                <p className={itemID === item.id ? "item-header-title-selected" : "item-header-title"}>{item.title}</p>
            </Link>
            <CloseIcon className={`item-header-close ${itemID === item.id && "blue"}`}
                        onClick={() => {closeItem()}} />
        </div>
    )
}

export default HeaderOpenItems
