import React, {useState, useEffect} from 'react'
import { Link, useHistory } from 'react-router-dom'
import itemsAPI from '../API/items'
import SearchIcon from '@mui/icons-material/Search';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import AddIcon from '@mui/icons-material/Add';

const Home = ({setSearchPhrase, currentUser}) => {
    const [recent, setRecent] = useState()
    const [loaded, setLoaded] = useState(false)
    const [input, setInput] = useState("");
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const recent = await itemsAPI.get("/recent");

                setRecent(recent.data.data);
                setLoaded(true);
            } catch (err) {}
        }
        fetchData();
    }, [])

    const searchFunctionKey = (e) => {
        if (e.key === "Enter" && input !== "") {
            setSearchPhrase(input);
            history.push(`/search-results/${input}`);
        }
    }

    const searchFunctionButton = () => {
        if (input !== "") {
            setSearchPhrase(input);
            history.push(`/search-results/${input}`);
        }
    }

    return (
        <>
            {loaded &&
                <div className="main-body">
                    <div className="sidebar">
                        <div className="home-search">
                            <input className="home-search-input" 
                                    placeholder="Search"
                                    value={input}
                                    onChange={e => setInput(e.target.value)}
                                    onKeyPress={searchFunctionKey} />
                            <SearchIcon className="home-search-icon" onClick={e => searchFunctionButton()} />
                        </div>
                        <Link className="home-options-link">
                            <p>Public Workspaces</p>
                            <ArrowForwardIosIcon className="home-options-icon-arrow" />
                        </Link>
                        <Link className="home-options-link">
                            <p>Public Datasets</p>
                            <ArrowForwardIosIcon className="home-options-icon-arrow" />
                        </Link>
                        <Link className="home-options-link">
                            <p>Your Bookmarks</p>
                            <ArrowForwardIosIcon className="home-options-icon-arrow" />
                        </Link>
                        <Link className="home-options-link">
                            <p>Your Workspaces</p>
                            <AddIcon className="home-options-icon-add" />
                        </Link>
                        <Link className="home-options-link">
                            <p>Your Datasets</p>
                            <AddIcon className="home-options-icon-add" />
                        </Link>
                    </div>
                    <div className="inner">
                        <h1>Recently Updated</h1>
                        <div className="toggle-card-type">
                            {(type === "created-workspaces" || type === "all-workspaces") ?
                                <p>{`${items.length} Workspaces`}</p>
                            : (type === "created-datasets" || type === "all-dataset") ?
                                <p>{`${items.length} Datasets`}</p>
                            :
                                <p>{`${items.length} Bookmarks`}</p>
                            }
                            <img src="http://localhost:3000/List.png" className="view-items-row-icon" onClick={() => {setRowFormat(true)}} />
                            <img src="http://localhost:3000/Grid.png" className="view-items-grid-icon" onClick={() => {setRowFormat(false)}} />
                        </div>
                    </div>
                </div>
            }
        </>
    )
}

export default Home
