import React, {useState, useEffect} from 'react'
import {useHistory, useParams} from "react-router-dom"
import usersAPI from '../API/users'
import ViewData from '../Components/View-Data'
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';

const CreateExperiment = ({currentUser}) => {
    const [stage, setStage] = useState(true);
    const [title, setTitle] = useState("");
    const [visibility, setVisibility] = useState(false);
    const [datasets, setDatasets] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [disableCreate, setDisabledCreate] = useState(false)
    const history = useHistory();
    const workspaceID = useParams().id;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const datasets = await usersAPI.get("/created-datasets");

                datasets.data.data.map((dataset) => {
                    setDatasets(state => [...state, dataset.title]);
                })
                setLoaded(true);
            } catch (err) {}
        }
        fetchData();
    }, [])

    const next = () => {
        if ((title !== "" || description !== "") && !datasets.includes(title)) {
            setStage(false)
        }
    }

    const cancel = () => {
        history.replace(`/${workspaceID}`);
    }

    return (
        <>
            {loaded &&
                <div className="sidebar-body">
                    <div className="create-sidebar">
                        <h1>Create Experiment</h1> 
                        <div className="create-item-header">
                            <p className="create-item-title">{title}</p> 
                        </div>
                        <button className={`${"create-sidebar-stage"} ${stage ? "create-sidebar-stage-selected" : "create-sidebar-stage-unselected"}`}
                                disabled={stage}
                                onClick={() => {setStage(true)}}>Setup</button>
                        <button className={`${"create-sidebar-stage"} ${stage ? "create-sidebar-stage-selected" : "create-sidebar-stage-unselected"}`}
                                disabled={stage}
                                onClick={() => {setStage(true)}}>Modelling</button>
                        <button className={`${"create-sidebar-stage"} ${stage ? "create-sidebar-stage-selected" : "create-sidebar-stage-unselected"}`}
                                disabled={stage}
                                onClick={() => {setStage(true)}}>Training</button>
                        <button className={`${"create-sidebar-stage"} ${!stage ? "create-sidebar-stage-selected" : "create-sidebar-stage-unselected"}`}
                                disabled>Evaluation</button>
                    </div>
                    <div className="inner-body">
                        <>
                            {stage &&
                                <>
                                    <div className="create-item-setup-information">
                                        <div className="create-item-nav">   
                                            <p className="create-item-filename">Experiment Information</p>
                                            <button className="white-button create-item-cancel"
                                                    onClick={() => {cancel()}}>Cancel</button>
                                            <button className="blue-button"
                                                    onClick={() => {next()}}>Next</button>
                                        </div>
                                        <input className="create-item-title"
                                                placeholder="Title"
                                                onChange={e => {setTitle(e.target.value)}}
                                                value={title} />=
                                        <div className="create-item-setup-visibility">
                                            <label className="create-item-setup-visibility-label">Public?</label>
                                            <input type="checkbox" 
                                                    onChange={() => {setVisibility(previous => !previous)}}
                                                    checked={visibility} />
                                        </div>
                                    </div>
                                </>
                            }
                        </>
                    </div>
                </div>
            }   
        </>
    )
}

export default CreateExperiment
