import React, {useState, useEffect} from 'react'
import {useHistory} from "react-router-dom"
import datasetsAPI from '../API/datasets'
import usersAPI from '../API/users'
import fileAPI from '../API/files'
import imageAPI from '../API/images'
import ViewData from '../Components/View-Data'

const NewDataset = ({currentUser}) => {
    const [setupStage, setSetupStage] = useState(true);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [visibility, setVisibility] = useState(false);
    const [dataFile, setDataFile] = useState();
    const [displayData, setDisplayData] = useState();
    const [image, setImage] = useState();
    const [datasets, setDatasets] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [disableCreate, setDisabledCreate] = useState(false)
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const datasets = await usersAPI.get("/created-datasets");

                datasets.data.data.map((dataset) => {
                    setDatasets(state => [...state, dataset.title]);
                })
                setLoaded(true);
            } catch (err) {}
        }
        fetchData();
    }, [])

    useEffect(() => {
        if (data !== undefined) {
            setDataID("")

            const file = data;
            const reader = new FileReader();

            reader.onload = function(e) {
                setDisplayData(e.target.result);
            }

            reader.readAsText(file)
        }
    }, [data])

    const next = () => {
        if ((title !== "" || description !== "") && !datasets.includes(title)) {
            setSetupStage(false)
        }
    }

    const cancel = () => {
        history.goBack();
    }

    const remove = () => {
        setDataFile()
        setDisplayData()
    }

    const uploadImage = async () => {
        setDisabledCreate(true)

        if (dataFile !== undefined) {
            if (image) {
                const formImage = new FormData();
                formImage.append('image', image);

                try {
                    const imageResponse = await imageAPI.post("/upload", formImage);

                    uploadDataset(imageResponse.data.data)
                } catch (err) {}
            } else {
                uploadDataset("default.png")
            }
        } else {
            setDisabledCreate(false)
        }
    }

    const uploadDataset = async (imageName) => {
        try {
            const datasetResponse = await datasetsAPI.post("/", {
                title: title,
                data: "none",
                creator: currentUser.id,
                description: description,
                experiments: [],
                picture: imageName,
                upvotes: 0,
                visibility: visibility
            });

            uploadFile(datasetResponse.data.data)
        } catch (err) {}
    }

    const uploadFile = async (workspaceID) => {
        const formData = new FormData();

        formData.append('data', data);
        formData.append('id', workspaceID)

        try {
            await fileAPI.post("/upload", formData);
        } catch (err) {}

        try {
            await datasetsAPI.put(`/${workspaceID}`, {
                data: `${workspaceID}-data`
            });

            history.push(`/workspace/${workspaceID}`)
        } catch (err) {}
    }

    return (
        <>
            {loaded &&
                <div className="new-dataset">
                    <div className="new-sidebar">
                        <h1>New Dataset</h1> 
                        <div className="new-dataset-header">
                            <p className="new-dataset-title">{title}</p> 
                        </div>
                        <button className={`${"new-sidebar-stage"} ${setupStage ? "new-sidebar-stage-selected" : "new-sidebar-stage-unselected"}`}
                                disabled={setupStage}
                                onClick={() => {setSetupStage(true)}}>Setup</button>
                        <button className={`${"new-sidebar-stage"} ${!setupStage ? "new-sidebar-stage-selected" : "new-sidebar-stage-unselected"}`}
                                disabled>Data</button>
                    </div>
                    { setupStage &&
                        <div className="new-dataset-setup">
                            <div className="new-dataset-setup-information">
                                <input className="new-dataset-title"
                                        placeholder="Title"
                                        onChange={e => {setTitle(e.target.value)}}
                                        value={title} />
                                <textarea className="new-dataset-description"
                                            placeholder="Description"
                                            onChange={e => {setDescription(e.target.value)}}
                                            value={description} />
                                <div className="new-dataset-setup-visibility">
                                    <label className="new-dataset-setup-visibility-label">Public?</label>
                                    <input type="checkbox" 
                                            onClick={() => {setVisibility(previous => !previous)}}
                                            checked={visibility} />
                                </div>
                                <div className="new-dataset-setup-visibility">
                                    <label className="new-dataset-setup-visibility-label">Picture</label>
                                    <input type="file" 
                                            name="image" 
                                            onChange={e => {setImage(e.target.files[0])}} />
                                </div>
                            </div>
                            <div className="new-dataset-nav">   
                                <button className="new-dataset-cancel"
                                        onClick={() => {cancel()}}>Cancel</button>
                                <button className="new-dataset-next"
                                        onClick={() => {next()}}>Next</button>
                            </div>
                        </div>
                    }
                    { !setupStage &&
                        <>
                            <div className="new-dataset-import">
                                <p>Import Data</p>
                                <input type="file" 
                                        name="data"
                                        key={Date.now()}
                                        onChange={e => {setDataFile(e.target.files[0])}} />
                                {dataFile !== undefined && displayData !== undefined &&
                                    <div className="new-dataset-data">
                                        <div className="new-dataset-data-information">
                                            <p className="new-dataset-filename">File: {dataFile.name}</p>
                                            <button className="new-dataset-cancel"
                                                    onClick={() => {remove()}}>Remove</button>
                                        </div>
                                        <div className="new-dataset-data-table">
                                            <ViewData displayData={displayData} />
                                        </div>
                                    </div>
                                }
                            </div>
                            <div className="new-dataset-nav">  
                                <button className="new-dataset-cancel"
                                        onClick={() => {cancel()}}>Cancel</button>
                                <button className="new-dataset-next"
                                        disabled={disableCreate}
                                        onClick={() => {uploadImage()}}>Create</button>
                            </div>
                        </>
                    }
                </div>
            }   
        </>
    )
}

export default NewDataset
