import React, {useState, useEffect, useContext} from 'react'
import { Link } from 'react-router-dom'
import { OpenWorkspacesContext } from '../Contexts/openWorkspacesContext';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import VisibilityIcon from '@mui/icons-material/Visibility';

const WorkspaceSquareCard = ({workspace, self}) => {
    const {addOpenWorkspaces} = useContext(OpenWorkspacesContext);
    const [date, setDate] = useState("");

    useEffect(() => {
        const updatedDate = new Date(workspace.fetchedWorkspaces.updatedAt);
        const currentDate = new Date();

        if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 365) {
            setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 365)).toString()} year(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 30) {
            setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString())} month(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 1) {
            setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24))).toString()} day(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600) >= 1) {
            setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600))).toString()} hour(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60) >= 1) {
            setDate(`Updated ${Math.floor(((currentDate.getTime() - updatedDate.getTime()) / (1000 * 60))).toString()} minute(s) ago`)
        }
    }, [])

    return (
        <Link to={`/workspace/${workspace.fetchedWorkspaces._id}`} className="workspace-square-card" onClick={() => {addOpenWorkspaces(workspace.fetchedWorkspaces._id, workspace.fetchedWorkspaces.title)}}>
            <img src={`http://localhost:4000/images/${workspace.fetchedWorkspaces.picture}`} />
            <p className="workspace-square-card-heading">{workspace.fetchedWorkspaces.title}</p>
            <div>
                {self && 
                    <>
                        {workspace.fetchedWorkspaces.visibility ? 
                            <VisibilityIcon className="workspace-square-card-visibility" />
                        :
                            <VisibilityOffIcon className="workspace-square-card-visibility" />
                        }
                    </>
                }
                {!self && <p className="workspace-square-card-meta">{workspace.creator.name}</p>}
                <p className="workspace-square-card-meta">{date}</p>
                {self && 
                    <>
                        <ThumbUpIcon className={`${workspace.fetchedWorkspaces.upvoted ? "blue" : "grey"} workspace-square-card-icon`} />
                        <p className="workspace-square-card-upvotes">{workspace.fetchedWorkspaces.upvotes}</p>
                    </>
                }
            </div>
            {!self &&
                <div className="workspace-square-card-other">
                    <BookmarkIcon className={`${workspace.fetchedWorkspaces.bookmarked ? "blue" : "grey"}workspace-square-card-icon`} />
                    <ThumbUpIcon className={`workspace-square-card-icon${workspace.fetchedWorkspaces.upvoted ? "blue" : "grey"}workspace-square-card-icon`} />
                    <p className="workspace-square-card-upvotes">{workspace.fetchedWorkspaces.upvotes}</p>
                </div>
            }
        </Link>
    )
}

export default WorkspaceSquareCard
