import React, {useState, useEffect, useContext} from 'react'
import usersAPI from '../API/users'
import authAPI from "../API/auth"
import Shortcut from '../Components/Shortcut';
import { CurrentUserContext } from '../Contexts/currentUserContext';

const Account = () => {
    const [user, setUser] = useState();
    const [loaded, setLoaded] = useState(false);
    const [password, setPassword] = useState("")
    const {removeCurrentUser} = useContext(CurrentUserContext);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const user = await usersAPI.get("/");

                setUser(user.data.data);
                setLoaded(true);
            } catch (err) {}
        }
        fetchData();
    }, [])

    const signout = async () => {
        await authAPI.get("/signout");

        removeCurrentUser();

        if (typeof window !== 'undefined') {
            window.location = `/sign-in`
        }
    }

    return (
        <>
            {loaded &&
                <div className="width-body">
                    <div className="home-left-column">
                        <Shortcut type="workspaces" />
                        <Shortcut type="datasets" />
                        <Shortcut type="bookmarked-workspaces" />
                        <Shortcut type="bookmarked-datasets" />
                    </div>
                    <div className="account-information">
                        <h1>Account Settings</h1>
                        <label>Name</label>
                        <p>{user.name}</p>
                        <label>Username</label>
                        <p>{user.username}</p>
                        <label>Email Address</label>
                        <p>{user.email}</p>
                        <label>Password</label>
                        <input placeholder="Change Password" value={password} onChange={e => {setPassword(e.target.value)}} />
                        <button className="blue-button account-save" disabled={password === ""}>Save Changes</button>
                        <button className="dark-grey-button account-delete">Delete Account</button>
                        <button className="grey-button account-signout" onClick={() => {signout()}}>Sign Out</button>
                    </div>
                </div>
            }
        </>
    )
}

export default Account
