import React, {useState, useEffect, useContext} from 'react'
import { Link } from 'react-router-dom'
import { OpenWorkspacesContext } from '../Contexts/openWorkspacesContext';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import VisibilityIcon from '@mui/icons-material/Visibility';

const WorkspaceSquareCard = ({workspace, self}) => {
    const {addOpenWorkspaces} = useContext(OpenWorkspacesContext);
    const [date, setDate] = useState("");

    useEffect(() => {
        const updatedDate = new Date(workspace.updatedAt);
        const currentDate = new Date();

        if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 365) {
            setDate(`Updated ${((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 365).toString()} year(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 30) {
            setDate(`Updated ${((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString()} month(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) >= 1) {
            setDate(`Updated ${((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24)).toString()} day(s) ago`)
        } else if ((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) > 30) {
            setDate(`Updated ${((currentDate.getTime() - updatedDate.getTime()) / (1000 * 3600 * 24) % 30).toString()} month(s) ago`)
        }
    }, [])

    return (
        <Link to={`/workspace/${workspace._id}`} className="workspace-square-card" onClick={() => {addOpenWorkspaces(workspace._id, workspace.title)}}>
            <img src={`http://localhost:4000/images/${workspace.picture}`} />
            <p className="workspace-square-card-heading">{workspace.title}</p>
            <div>
                {self && 
                    <>
                        {workspace.visibility ? 
                            <VisibilityIcon className="workspace-square-card-visibility" />
                        :
                            <VisibilityOffIcon className="workspace-square-card-visibility" />
                        }
                    </>
                }
                {!self && <p className="workspace-square-card-meta">{workspace.name}</p>}
                <p className="workspace-square-card-meta">Updated 2 hours ago</p>
                {self && 
                    <>
                        <ThumbUpIcon className="workspace-square-card-icon" />
                        <p className="workspace-square-card-upvotes">{workspace.upvotes}</p>
                    </>
                }
            </div>
            {!self &&
                <div className="workspace-square-card-other">
                    <BookmarkIcon className="workspace-square-card-icon" />
                    <ThumbUpIcon className="workspace-square-card-icon" />
                    <p className="workspace-square-card-upvotes">{workspace.upvotes}</p>
                </div>
            }
        </Link>
    )
}

export default WorkspaceSquareCard
