import React, {useState, useEffect} from 'react'
import {useHistory} from "react-router-dom"
import projectsAPI from '../API/projects'
import fileAPI from '../API/files'
import imageAPI from '../API/images'
import ViewData from '../Components/View-Data'

const NewProject = ({currentUser}) => {
    const [setupStage, setSetupStage] = useState(true);
    const [dataStage, setDataStage] = useState(false);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [visibility, setVisibility] = useState(false);
    const [data, setData] = useState();
    const [displayData, setDisplayData] = useState();
    const [importMethod, setImportMethod] = useState("")
    const [image, setImage] = useState();
    const [dataID, setDataID] = useState("")
    const [projects, setProjects] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const projects = await projectsAPI.get("/");

                projects.data.data.map((project) => {
                    setProjects(previous => [...previous, project.title]);
                })
                setLoaded(true);
            } catch (err) {}
        }
        fetchData();
    }, [])

    useEffect(() => {
        if (data !== undefined) {
            const file = data;
            const reader = new FileReader();

            reader.onload = function(e) {
                setDisplayData(e.target.result);
            }

            reader.readAsText(file)
        }
    }, [data])

    const next = () => {
        if ((title !== "" || description !== "") && !projects.includes(title)) {
            setSetupStage(false)
            setDataStage(true)
        }
    }

    const changeStage = () => {
        setSetupStage(false)
        setDataStage(true)
    }

    const cancel = () => {
        history.goBack();
    }

    const uploadImage = async () => {
        if (image) {
            const formImage = new FormData();
            formImage.append('image', image);

            try {
                const imageResponse = await imageAPI.post("/upload", formImage);

                uploadData(imageResponse.data.data)
            } catch (err) {}
        } else {
            uploadData("default.png")
        }
    }

    const uploadData = async (imageName) => {
        try {
            const projectResponse = await projectsAPI.post("/", {
                title: title,
                data: "none",
                creator: currentUser.id,
                description: description,
                experiments: [],
                picture: imageName,
                upvotes: 0,
                visibility: visibility
            });

            uploadFile(projectResponse.data.data)
        } catch (err) {}
    }

    const uploadFile = async (projectID) => {
        if (importMethod !== "") {
            let dataName = ""

            if (importMethod === "file" && data !== undefined) {
                const formData = new FormData();

                formData.append('data', data);
                formData.append('id', projectID)

                try {
                    await fileAPI.post("/upload", formData);
                } catch (err) {}

                dataName = `${projectID}-data`
            } else if (importMethod === "existing" && dataID !== "") {
                dataName = dataID
            }

            try {
                await projectsAPI.put(`/${projectID}`, {
                    data: dataName
                });
            } catch (err) {}
        }

        history.push(`/project/${projectID}`)
    }

    return (
        <>
            {loaded &&
                <div className="new-project">
                    <div className="sidebar">
                        <h1>New Project</h1> 
                        <div className="new-project-header">
                            <p className="new-project-title">{title}</p> 
                        </div>
                        <button className={`${"sidebar-stage"} ${setupStage ? "sidebar-stage-selected" : "sidebar-stage-unselected"}`}
                                disabled={setupStage}
                                onClick={() => {changeStage()}}>Setup</button>
                        <button className={`${"sidebar-stage"} ${dataStage ? "sidebar-stage-selected" : "sidebar-stage-unselected"}`}
                                disabled={dataStage || setupStage}>Data</button>
                    </div>
                    { setupStage &&
                        <div className="new-project-setup">
                            <div className="new-project-setup-information">
                                <input className="new-project-title"
                                        placeholder="Title"
                                        onChange={e => {setTitle(e.target.value)}}
                                        value={title} />
                                <textarea className="new-project-description"
                                            placeholder="Description"
                                            onChange={e => {setDescription(e.target.value)}}
                                            value={description} />
                                <div className="new-project-setup-visibility">
                                    <label className="new-project-setup-visibility-label">Public?</label>
                                    <input type="checkbox" 
                                            onClick={() => {setVisibility(previous => !previous)}}
                                            value={visibility} />
                                </div>
                                <div className="new-project-setup-visibility">
                                    <label className="new-project-setup-visibility-label">Picture</label>
                                    <input type="file" 
                                            name="image" 
                                            onChange={e => {setImage(e.target.files[0])}} />
                                </div>
                            </div>
                            <div className="new-project-nav">   
                                <button className="new-project-cancel"
                                        onClick={() => {cancel()}}>Cancel</button>
                                <button className="new-project-next"
                                        onClick={() => {next()}}>Next</button>
                            </div>
                        </div>
                    }
                    { dataStage &&
                        <>
                            <div className="new-project-import">
                                <div className="new-project-import-options">
                                    <p>Import Data</p>
                                    <button onClick={() => {setImportMethod("existing")}}>Existing Project</button>
                                    <button onClick={() => {setImportMethod("file")}}>Upload File</button>
                                </div>
                                <div className="new-project-import-method">
                                {importMethod !== "" &&
                                    <>
                                        {importMethod === "file" ?
                                            <input type="file" 
                                                    name="data" 
                                                    onChange={e => {setData(e.target.files[0])}} />
                                        :
                                            <input className="new-project-import-projectid"
                                                    placeholder="Project ID"
                                                    onChange={e => {setDataID(e.target.value)}}
                                                    value={dataID} />
                                        }
                                    </>
                                }
                                </div>
                                {data !== undefined && displayData !== undefined &&
                                    <div className="new-project-data">
                                        <p className="new-project-filename">File: {data.name}</p>
                                        <div className="new-project-data-table">
                                            <ViewData displayData={displayData} />
                                        </div>
                                    </div>
                                }
                            </div>
                            <div className="new-project-nav">  
                                <button className="new-project-cancel"
                                        onClick={() => {cancel()}}>Cancel</button>
                                <button className="new-project-next"
                                        onClick={() => {uploadImage()}}>Create</button>
                            </div>
                        </>
                    }
                </div>
            }   
        </>
    )
}

export default NewProject
