import React, {useState, useEffect} from 'react'
import {useHistory, Link, useParams} from "react-router-dom"
import workspacesAPI from '../API/workspaces'

const Workspace = () => {
    const [loaded, setLoaded] = useState(false)
    const [workspace, setWorkspace] = useState()
    const workspaceID = useParams().id;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const workspace = await workspacesAPI.get(`/${workspaceID}`);

                setWorkspace(workspace.data.data);
                setLoaded(true)
            } catch (err) {}
        }
        fetchData();
    }, [])

    console.log(workspace)

    return (
        <>
            {loaded &&
                <>
                    {workspace.self ?
                        <div className="sidebar-body"> 
                            <div className="sidebar">
                                <h1>hi</h1>
                            </div>
                            <div className="inner-body">
                                <p>bye</p>
                            </div>
                        </div>
                    :
                        <div className="width-body">  
                            <div className="other-workspace-body">
                                <div className="other-workspace-top">
                                    <img src={`http://localhost:4000/images/${workspace.picture}`} />
                                    <h1>{workspace.title}</h1>
                                    <div>
                                        <p className="feed-workspace-meta">{creator}</p>
                                        <p className="feed-workspace-meta">{date}</p>
                                        <BookmarkIcon className={`feed-workspace-icon ${workspace.bookmarked ? "blue" : "grey"}`} />
                                        <ThumbUpIcon className={`feed-workspace-icon ${workspace.upvoted ? "blue" : "grey"}`} />
                                        <p className={`feed-workspace-upvotes ${workspace.upvoted ? "blue" : "grey"}`}>{workspace.upvotes}</p>
                                    </div>
                                </div>
                                <div className="other-workspace-bottom">

                                </div>
                            </div>
                        </div>
                    }
                </>
            }
        </>
    )
}

export default Workspace
